INSERT INTO webshop.comments (CommentID, Nickname, Rating, Message, Date, ProductID) VALUES (49, "Дмитрий", 0, "При игре в гта5 кочегарилась до 85 градусов и это зимой в продуваемом корпусе. Так что лучше приделать маленький обдув. Цена на уровне gt1030, которая на 30-40% абсолютно не оправдана. Достоинства: Тихая, DDR5 Недостатки: ЦЕНА!, устаревший чип, нагрев.", "2017-10-5", 20);
INSERT INTO webshop.products (ProductID, ProductTypeID, Title, Description, Price) VALUES (21, 6, "Asus PCI-Ex GeForce GT 730 2048MB DDR3 (64bit) (902/1600) (VGA, DVI, HDMI) (GT730-SL-2G-BRK-V2)", "Качественная графика, доступная каждому! Компания Asus представляет видеокарту GT 730, которая оснащается бесшумной системой охлаждения, что делает ее идеальным выбором для компактных HTPC-компьютеров. Данная модель также обладает низким уровнем электромагнитного излучения за счет специального экранирования и является совместимой с утилитой GPU Tweak II для интуитивной настройки производительности. Абсолютно бесшумная работа Радиатор с большой площадью рассеивания тепла справляется с охлаждением видеокарты без применения вентиляторов. Таким образом, данное устройство является абсолютно бесшумным, что делает его идеальным выбором для домашних мультимедийных компьютеров. Компоненты Super Alloy Power II Видеокарты Asus производятся с использованием технологии Auto-Extreme, первого в отрасли 100% автоматизированного производственного процесса, а также обладают превосходными компонентами Super Alloy Power II, которые повышают эффективность, уменьшают потери мощности, уменьшают жужжание компонентов под нагрузкой и более низкие тепловые температуры для непревзойденного качества и надежность. GPU Tweak II Обновленный с интуитивно понятным, совершенно новым пользовательским интерфейсом, GPU Tweak II делает разгон более простым и более визуальным, чем когда-либо, при этом сохраняя дополнительные возможности для опытных оверклокеров. С помощью одного щелчка новая функция Gaming Booster максимизирует производительность системы, удаляя избыточные процессы и автоматически распределяя все доступные ресурсы.", 15664);
