INSERT INTO webshop.comments (CommentID, Nickname, Rating, Message, Date, ProductID) VALUES (195, "Алексей Квасницкий", 4, "Отличная карточка,приобрел для апгрейда ”не молодого системника”, на замену GeForce GTX 560 TI 1GB, остановился на Gigabyte, так как последнии карты были этой фирмы, вроде все нормально работает.Из плюсов - потребление, почти в два раза меньше использует электричества, чем моя старая карта. Бонусом... Еще 3 года гарантии, что есть тоже хорошо. #мояраспаковка Достоинства: Мощная, для среднего ПК отлично подойдет, Потребление (около 75 Ватт),3 года гарантии. Недостатки: Из-за бума майнинга цены завышены, хотя понимаю, что это ”Just business” (@)", "2018-1-3", 74);
