INSERT INTO webshop.comments (CommentID, Nickname, Rating, Message, Date, ProductID) VALUES (232, "Гость", 5, "Купил 2 такие карты, БП на 750w справляется без проблем. Корпус не большой, поэтому карты стоят прямо одна над другой, во время рендера верхняя карта 66градусов, нижняя 71 (пришлось открыть боковую крышку корпуса). Кулеры крутятся где-то на 40 %, при этом карты относительно тихие, я даже сплю во... Еще время рендера и звук не мешает даже ночью ;) Заметил что после установки второй карты в корпусе иногда появляется звук, похожий на звук жесткого диска, не пойму что это такое, карта может издавать такой звук? В корпусе только ssd стоит, а такой звук появился именно после установки второй карточки...", "2018-1-2", 88);
